mapper.sql:
	WHERE DELETED = 0 
 			 AND ID = #{id} 
 			 AND USERNAME = #{username} 
 			 AND OPEN_ID = #{openId} 
 			 AND MOBILE = #{mobile} 
 			 AND SELLER_ID = #{sellerId} 
 			 AND REMARK = #{remark} 
 			 AND STATE = #{state} 
 			 AND CREATED_TIME >= #{param.createdTimeStart}  
 			 AND CREATED_TIME <= #{param.createdTimeEnd} 
 			 AND MODIFIED_TIME >= #{param.modifiedTimeStart}  
 			 AND MODIFIED_TIME <= #{param.modifiedTimeEnd} 
			 AND LAST_UPDATE_NO = #{lastUpdateNo} 
 			 AND LAST_UPDATE_NO >= #{param.lastUpdateNoStart}  
 			 AND LAST_UPDATE_NO <= #{param.lastUpdateNoEnd} 
 			 AND LAST_UPDATE_NO_USER_ID = #{lastUpdateNoUserId}

mapper.select:
SELECT   

FROM   sign_student

ORDER BY ${param.columnSort}   
ORDER BY ID    
limit ${param.offset}, ${param.limit}
