mapper.sql:
	WHERE DELETED = 0 
 			 AND ID = #{id} 
 			 AND OPEN_ID = #{openId} 
 			 AND USERNAME = #{username} 
 			 AND MOBILE = #{mobile, typeHandler=com.nbs.jiaxiao.component.CryptTypeHandler} 
 			 AND CERT_NO = #{certNo, typeHandler=com.nbs.jiaxiao.component.CryptTypeHandler} 
 			 AND DRIVE_TYPE = #{driveType} 
 			 AND TOTAL_FEE = #{totalFee} 
 			 AND SIGN_DATE = #{signDate} 
 			 AND SELL_ID = #{sellId} 
 			 AND STAGE = #{stage} 
 			 AND IS_ARREARAGE = #{isArrearage} 
 			 AND PHASE = #{phase} 
 			 AND REMARK = #{remark} 
 			 AND CREATED_TIME >= #{param.createdTimeStart}  
 			 AND CREATED_TIME <= #{param.createdTimeEnd} 
 			 AND MODIFIED_TIME >= #{param.modifiedTimeStart}  
 			 AND MODIFIED_TIME <= #{param.modifiedTimeEnd} 
			 AND LAST_UPDATE_NO = #{lastUpdateNo} 
 			 AND LAST_UPDATE_NO >= #{param.lastUpdateNoStart}  
 			 AND LAST_UPDATE_NO <= #{param.lastUpdateNoEnd} 
 			 AND LAST_UPDATE_NO_USER_ID = #{lastUpdateNoUserId}
